import React from 'react';
import ReactDOM from 'react-dom';
import 'antd/dist/antd.css' 
import './App.css';

import { Table, Button } from 'antd';


export default class App extends React.Component {

  state = {
    products : [],
    subCategories : [],
    categories : [],
  }

  componentDidMount() {
      const apiUrl = 'http://127.0.0.1:8000/product_list/';
      fetch(apiUrl)
        .then((response) => response.json())
        .then((data) => 
            this.setState({
              products: data
            })
      );
      const apiUrl2 = 'http://127.0.0.1:8000/sub_category_list/';
      fetch(apiUrl2)
        .then((response) => response.json())
        .then((data) => 
            this.setState({
              subCategories: data
            })
      );
      const apiUrl3 = 'http://127.0.0.1:8000/category_list/';
      fetch(apiUrl3)
        .then((response) => response.json())
        .then((data) => 
            this.setState({
              categories: data
            })
      );
  }


  state = {
    filteredInfo: null,
    sortedInfo: null,
  };
  handleChange = (pagination, filters, sorter) => {
    console.log('Various parameters', pagination, filters, sorter);
    this.setState({
      filteredInfo: filters,
      sortedInfo: sorter,
    });
  }
  clearFilters = () => {
    this.setState({ filteredInfo: null });
  }
  clearAll = () => {
    this.setState({
      filteredInfo: null,
      sortedInfo: null,
    });
  }
  setAgeSort = () => {
    this.setState({
      sortedInfo: {
        order: 'descend',
        columnKey: 'age',
      },
    });
  }
  render() {
    let { sortedInfo, filteredInfo, products,categories,subCategories } = this.state;
    console.log(categories);
    sortedInfo = sortedInfo || {};
    filteredInfo = filteredInfo || {};
    const columns = [{
      title: 'Name',
      dataIndex: 'name',
      key: 'name',
      sorter: (a, b) => a.name.length - b.name.length,
      sortOrder: sortedInfo.columnKey === 'name' && sortedInfo.order,
    }, {
      title: 'Category',
      render: (data) => data.sub_category.category.name,
      key: 'category',
      sorter: (a, b) => a.sub_category.category.name.length - b.sub_category.category.name.length,
      sortOrder: sortedInfo.columnKey === 'category' && sortedInfo.order,
    }, {
      title: 'Sub Category',
      render: (data) => data.sub_category.name,
      key: 'sub_category',
      // filters: [
      //   { text: 'London', value: 'London' },
      //   { text: 'New York', value: 'New York' },
      // ],
      // filteredValue: filteredInfo.address || null,
      // onFilter: (value, record) => record.address.includes(value),
      sorter: (a, b) => a.sub_category.name.length - b.sub_category.name.length,
      sortOrder: sortedInfo.columnKey === 'sub_category' && sortedInfo.order,
    }];
    return (
      <div>
        <div className="table-operations">
          <Button onClick={this.clearFilters}>Clear filters</Button>
          <Button onClick={this.clearAll}>Clear filters and sorters</Button>
        </div>
        <Table columns={columns} dataSource={products} onChange={this.handleChange} />
      </div>
    );
  }
}

ReactDOM.render(<App />, document.getElementById('root'));
