import React from 'react';
import ReactDOM from 'react-dom';
import 'antd/dist/antd.css' 
import './App.css';

import { Table, Button } from 'antd';


export default class App extends React.Component {

  state = {
    products : [],
    subCategories : [],
    categories : [],
    sortedInfo: null,
  }

  componentDidMount() {
      const apiUrl = 'http://127.0.0.1:8000/product_list/';
      fetch(apiUrl)
        .then((response) => response.json())
        .then((data) => 
            this.setState({
              products: data
            })
      );
      const apiUrl2 = 'http://127.0.0.1:8000/sub_category_list/';
      fetch(apiUrl2)
        .then((response) => response.json())
        .then((data) => 
            this.setState({
              subCategories: data
            })
      );
      const apiUrl3 = 'http://127.0.0.1:8000/category_list/';
      fetch(apiUrl3)
        .then((response) => response.json())
        .then((data) => 
            this.setState({
              categories: data
            })
      );
  }

  handleChange = (pagination, sorter) => {
    console.log('Various parameters', pagination, sorter);
    this.setState({
      sortedInfo: sorter,
    });
  }

  clearAll = () => {
    this.setState({
      sortedInfo: null,
    });
  }

  render() {
    let { sortedInfo, products,categories,subCategories } = this.state;
    console.log(categories);

    let categoryFilter = [];
    let subCategoryFilter = [];

    if(categories){
      categories.map((item, key) =>
        categoryFilter.push(
          { text: item.name , value: item.name },
        ),
      );
    }

    if(subCategories){
      subCategories.map((item, key) =>
      subCategoryFilter.push(
          { text: item.name , value: item.name },
        ),
      );
    }


    sortedInfo = sortedInfo || {};

    const columns = [{
      title: 'Name',
      dataIndex: 'name',
      key: 'name',
      sorter: (a, b) => a.name.length - b.name.length,
      sortOrder: sortedInfo.columnKey === 'name' && sortedInfo.order,
    },  {
      title: 'Sub Category',
      render: (data) => data.sub_category.name,
      key: 'sub_category',
      filters: subCategoryFilter,
      onFilter: (value, record) => record.sub_category.name.includes(value),
      sorter: (a, b) => a.sub_category.name.length - b.sub_category.name.length,
      sortOrder: sortedInfo.columnKey === 'sub_category' && sortedInfo.order,
    },{
      title: 'Category',
      render: (data) => data.sub_category.category.name,
      key: 'category',
      filters: categoryFilter,
      onFilter: (value, record) => record.sub_category.category.name.includes(value),
      sorter: (a, b) => a.sub_category.category.name.length - b.sub_category.category.name.length,
      sortOrder: sortedInfo.columnKey === 'category' && sortedInfo.order,
    }];
    return (
      <div>
        <div className="table-operations">
          <Button onClick={this.clearFilters}>Clear Sorters</Button>
        </div>
        <Table columns={columns} dataSource={products} onChange={this.handleChange} />
      </div>
    );
  }
}

ReactDOM.render(<App />, document.getElementById('root'));
