import React from 'react';
import ReactDOM from 'react-dom';
import 'antd/dist/antd.css' 
import './App.css';

import {Button, Table,Modal, Form, Input, Select  } from 'antd';
import { FormInstance } from 'antd/lib/form';

const { Option } = Select;


export default class App extends React.Component {

  state = {
    products : [],
    subCategories : [],
    categories : [],
    visible: false,
  }

  componentDidMount() {
      const apiUrl = 'http://127.0.0.1:8000/product_list/';
      fetch(apiUrl)
        .then((response) => response.json())
        .then((data) => 
            this.setState({
              products: data
            })
      );
      const apiUrl2 = 'http://127.0.0.1:8000/sub_category_list/';
      fetch(apiUrl2)
        .then((response) => response.json())
        .then((data) => 
            this.setState({
              subCategories: data
            })
      );
      const apiUrl3 = 'http://127.0.0.1:8000/category_list/';
      fetch(apiUrl3)
        .then((response) => response.json())
        .then((data) => 
            this.setState({
              categories: data
            })
      );
  }

  showModal = () => {
    this.setState({
      visible: true,
    });
  };

  handleOk = e => {
    console.log(e);
    this.setState({
      visible: false,
    });
  };

  handleCancel = e => {
    console.log(e);
    this.setState({
      visible: false,
    });
  };


  render() {
    let { products,categories,subCategories } = this.state;

    let categoryFilter = [];
    let subCategoryFilter = [];
    let dropDownMix = [];


    if(categories){
      categories.map((item, key) =>
        categoryFilter.push(
          { text: item.name , value: item.name },
        ),
      );
    }

    if(subCategories){
      subCategories.map((item, key) =>
        dropDownMix.push(
          <Option value={item.id}>{item.name} , {item.category.name}</Option>,
        ),
      );


      subCategories.map((item, key) =>
        subCategoryFilter.push(
          { text: item.name , value: item.name },
        ),
      );
    }


    const columns = [{
      title: 'Name',
      dataIndex: 'name',
      key: 'name',
      sorter: (a, b) => { return a.name.localeCompare(b.name)},
      sortDirections: ['descend', 'ascend'],
    },  {
      title: 'Sub Category',
      render: (data) => data.sub_category.name,
      key: 'sub_category',
      filters: subCategoryFilter,
      onFilter: (value, record) => record.sub_category.name.includes(value),
      sorter: (a, b) => { return a.sub_category.name.localeCompare(b.sub_category.name)},
      sortDirections: ['descend', 'ascend'],
    },{
      title: 'Category',
      render: (data) => data.sub_category.category.name,
      key: 'category',
      filters: categoryFilter,
      onFilter: (value, record) => record.sub_category.category.name.includes(value),
      sorter: (a, b) => { return a.sub_category.category.name.localeCompare(b.sub_category.category.name)},
      sortDirections: ['descend', 'ascend'],
    }];
    return (
      <div>
        <div className="table-operations">
          <Button onClick={this.showModal}>Add</Button>
        </div>
        <Table columns={columns} dataSource={products} />
        <Modal
          title="Add Product"
          visible={this.state.visible}
          onOk={this.handleOk}
          onCancel={this.handleCancel}
        >
         <Form >
            <Form.Item name="note" label="Note" rules={[{ required: true }]}>
              <Input />
            </Form.Item>
            <Form.Item name="gender" label="Sub Category" rules={[{ required: true }]}>
              <Select
                placeholder="Select a option"
                allowClear
                showSearch
                filterOption={(input, option) =>
                  option.children.toLowerCase().indexOf(input.toLowerCase()) >= 0
                }
              >
               {dropDownMix}
              </Select>
            </Form.Item>
            </Form>
        </Modal>

      </div>
    );
  }
}

ReactDOM.render(<App />, document.getElementById('root'));
